import classes from "./Counter.module.css";
import { useSelector, useDispatch } from "react-redux";
import { increment, decrement } from "../store/index";
import {useState} from 'react'



const Counter = () => {
  const[ userInput, setUserInput] = useState("");

	const dispatch = useDispatch();
	const counter = useSelector((state) => state.counter);

	const toggleCounterHandler = () => {};

	return (
		<main className={classes.counter}>
			<h1>Tanner's Redux Counter app</h1>
			<div className={classes.value}>{counter.counter}</div>
      <input onChange={(event)=> setUserInput(event.target.value)} placeholder=""/>

			<div className={classes.buttons}>
				<button
					aria-label='decrement'
					onClick={() => dispatch({ type: "decrement" })}>
					-
				</button>
				<button onClick={() => dispatch({ type: "increase", value: Number(userInput) })}>
					Increase By Input Amount
				</button>
				<button
					aria-label='increment'
					onClick={() => dispatch({ type: "increment" })}>
					+
				</button>
			</div>
			<br></br>
			<button onClick={toggleCounterHandler}>Toggle Counter</button>
		</main>
	);
};

export default Counter;
